mapper.select:
SELECT * FROM( SELECT MESSAGE_ID messageId, SENDER_NO senderNo, USER_NAME senderName, CONTENT content, SENT_DATE sentDate FROM TB_TEAM_MESSAGE JOIN TB_USER ON (USER_NO = SENDER_NO) WHERE TEAM_ID = #{teamId}
AND SENT_DATE < TO_TIMESTAMP(#{lastTimeStamp}, 'YYYY-MM-DD HH24:MI:SS.FF3')
ORDER BY SENT_DATE DESC ) WHERE ROWNUM <= 20 ORDER BY sentDate ASC

mapper.update:
UPDATE
	TB_TEAM_MESSAGE
SET
	CONTENT = #{content}
WHERE
	MESSAGE_ID = #{messageId}
